mapper.select:
SELECT A.BOARD_ID, A.BOARD_SJ, A.BOARD_CN, A.INQIRE_CO, A.CREAT_IP, A.NOTICE_AT, A.OTHBC_AT, A.USE_AT, A.ATCH_FILE_ID, A.FRST_REGIST_PNTTM, A.FRST_REGISTER_ID, ( SELECT CONCAT(X.STRE_FILE_NM, '.', X.FILE_EXTSN) FROM LETTNFILEDETAIL X WHERE X.ATCH_FILE_ID = A.ATCH_FILE_ID AND UPPER(FILE_EXTSN) IN ('GIF', 'JPG', 'JPEG', 'BMP', 'PNG') ORDER BY FILE_SN LIMIT 1 ) AS ATCH_FILE_NM FROM BOARD A
ORDER BY A.FRST_REGIST_PNTTM DESC
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.update:
UPDATE BOARD
SET BOARD_SJ = #{boardSj},
	BOARD_CN = #{boardCn},
	NOTICE_AT = #{noticeAt},
	OTHBC_AT = #{othbcAt},

		ATCH_FILE_ID = #{atchFileId},

	LAST_UPDT_PNTTM = NOW(),
	LAST_UPDUSR_ID = #{userId}
WHERE BOARD_ID = #{boardId}

	AND FRST_REGISTER_ID = #{userId}

UPDATE BOARD
SET USE_AT = 'N'
WHERE BOARD_ID = #{boardId}

	AND FRST_REGISTER_ID = #{userId}
