mapper.select:
SELECT COUNT(*) CNT
FROM RESVEREQSTINFO A
WHERE RESVE_ID = #{resveId}
AND FRST_REGISTER_ID = #{userId}
AND USE_AT = 'Y'

SELECT A.REQST_ID, A.RESVE_ID, A.RESVE_DE, A.CHARGER_NM, A.TELNO, A.EMAIL, A.CONFM_SE_CODE, A.CONFMER_ID, A.RETURN_RESN, A.CONFM_PNTTM, A.CREAT_IP, A.USE_AT, A.FRST_REGIST_PNTTM, A.FRST_REGISTER_ID, B.RESVE_SJ, DATE_FORMAT(A.FRST_REGIST_PNTTM, '%Y-%m-%d') AS FRST_REGIST_PNTTM_YMD FROM RESVEREQSTINFO A LEFT OUTER JOIN RESVEINFO B ON A.RESVE_ID = B.RESVE_ID
ORDER BY A.FRST_REGIST_PNTTM DESC
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.update:
UPDATE RESVEREQSTINFO SET CHARGER_NM = #{chargerNm}, TELNO = #{telno}, EMAIL = #{email},
CONFM_SE_CODE = #{confmSeCode},
LAST_UPDT_PNTTM = NOW(), LAST_UPDUSR_ID = #{userId} WHERE REQST_ID = #{reqstId}

UPDATE RESVEREQSTINFO SET
		USE_AT = 'N'
WHERE REQST_ID = #{reqstId}
